import * as React from 'react';
import { styled } from '@mui/material/styles';
import {Box,Button, Paper, Grid, Rating, Tabs, Tab, Typography } from '@mui/material';
import PropTypes from 'prop-types';
import Product from './Product';



const Item = styled(Paper)(({ theme }) => ({
  backgroundColor: theme.palette.mode === 'dark' ? '#1A2027' : '#fff',
  ...theme.typography.body2,
  padding: theme.spacing(1),
  textAlign: 'center',
  color: theme.palette.text.secondary,
}));
///Users/satoshiaikawa/react-mui-bookstore-k8s/src/assets/jacket.gif
export default function FullWidthGrid() {
  return (
    <Paper elevation={1} sx={{
      flexGrow: 1,
      backgroundColor: (theme) => theme.palette.mode === 'dark' ? '#1A2027' : '#fff',
    }} >
      <Grid container spacing={2} >
        <Grid item md={12} >
          <Item elevation={0} ><img width="360" height="360"  src="https://cdn.shop-list.com/res/up/shoplist/shp/__thum370__/ueno-shokai/435121302-60/1.jpg" alt="海の写真" title="空と海"/></Item>
        </Grid>
        <Grid container justify="center" spacing={5} >
        {[1,2,3,4,5,6,7,8].map((product,idx) => (
            <Grid item key={idx} lg={1} >
             <img width="60" height="60"  src="https://cdn.shop-list.com/res/up/shoplist/shp/__thum370__/ueno-shokai/435121302-60/1.jpg" alt="海の写真" title="空と海"/>
            </Grid>
          ))}
        </Grid>
        <Grid item md={12} >
          <Item elevation={0} sx={{height:30,}}>NEW【即納】シンプルビッグダウンOt1152</Item>
        </Grid>
        <Grid item xs={6} md={4}>
          <Item elevation={0} sx={{backgroundColor:'white', height:30,}}><Rating name="size-small" defaultValue={Math.floor(Math.random() * (5 - 1 + 1) + 1)} size="small" readOnly />
        ({Math.floor(Math.random() * (200 - 10 + 10) + 10)})</Item>
        </Grid>
        <Grid item xs={6} md={8}>
          <Item elevation={0} sx={{ height:30,}}>¥9,660</Item>
        </Grid>
        <Grid item md={6}>
          <Item elevation={0} sx={{ height:30,}}><Button variant="outlined">商品詳細</Button></Item>
        </Grid>
        <Grid item md={6}>
          <Item elevation={0} sx={{ height:30,}}><Button variant="contained">お取り扱い</Button></Item>
        </Grid>
        <Grid item md={12}>
          <Item elevation={0} sx={{ height:30,}}><Button variant="contained">カートに入れる</Button></Item>
        </Grid>
        <Grid item md={12}>
          <Item elevation={0} sx={{ }}><BasicTabs /></Item>
        </Grid>
        <Grid item md={12}>
          <Item elevation={0} sx={{ height:30,}}>おすすめ関連アイテム</Item>
        </Grid>
        <Grid container justify="center" spacing={5} >
        {[1,2,3,4,5,6,7,8].map((product,idx) => (
            <Grid item key={idx} lg={3} >
            <Product />
            </Grid>
          ))}
        </Grid>
        <Grid item md={12}>
          <Item elevation={0} sx={{ height:30,}}>この商品のレビュー</Item>
        </Grid>
      </Grid>
    </Paper>
  );
}



let text="        シックな印象のスリムスラックス🇰🇷  <br> \
スリムなサイズ感なので履くだけで脚が長く見えます💪     \
\
シャツやジャケットなどと合わせる綺麗めコーデがお好きな方にもおすすめ◎ \
\
サイズ \
【M】\
長さ：100cm/ヒップ：95cm/ウエスト：80cm \
【L】\
長さ：101cm/ヒップ：99cm/ウエスト：84cm \
【XL】\
長さ：102cm/ヒップ：102cm/ウエスト：88cm \
【2XL】\
長さ：103cm/ヒップ：103cm/ウエスト：92cm \
\
参考推奨サイズ \
(M) 161~170cm/50~60kg \
(L) 170~175cm/60~65kg \
(XL) 175~185cm/65~77kg \
(2XL) 180~190cm/75~90kg \
※採寸には測定方法が異なるため多少の誤差 (1～3cm程)がある場合がございます。"


function TabPanel(props) {
  const { children, value, index, ...other } = props;

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`simple-tabpanel-${index}`}
      aria-labelledby={`simple-tab-${index}`}
      {...other}
    >
      {value === index && (
        <Box sx={{ p: 3 }}>
          <Typography>{children}</Typography>
        </Box>
      )}
    </div>
  );
}

TabPanel.propTypes = {
  children: PropTypes.node,
  index: PropTypes.number.isRequired,
  value: PropTypes.number.isRequired,
};

function a11yProps(index) {
  return {
    id: `simple-tab-${index}`,
    'aria-controls': `simple-tabpanel-${index}`,
  };
}

function BasicTabs() {
  const [value, setValue] = React.useState(0);

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  return (
    <Box sx={{ width: '100%' }} >
      <Box sx={{ borderBottom: 1, borderColor: 'divider' }}>
        <Tabs value={value} onChange={handleChange} aria-label="basic tabs example">
          <Tab label="商品詳細" {...a11yProps(0)} />
          <Tab label="お取り扱い" {...a11yProps(1)} />
        </Tabs>
      </Box>
      <TabPanel value={value} index={0}>
