import * as React from 'react';
import { styled } from '@mui/material/styles';
import {Box,Button, Paper, Grid} from '@mui/material';

const Item = styled(Paper)(({ theme }) => ({
  backgroundColor: theme.palette.mode === 'dark' ? '#1A2027' : '#fff',
  ...theme.typography.body2,
  padding: theme.spacing(1),
  textAlign: 'center',
  color: theme.palette.text.secondary,
}));
///Users/satoshiaikawa/react-mui-bookstore-k8s/src/assets/jacket.gif
export default function FullWidthGrid() {
  return (
    <Paper elevation={1} sx={{
      flexGrow: 1,
      backgroundColor: (theme) => theme.palette.mode === 'dark' ? '#1A2027' : '#fff',
      width:'25%'
    }} >
      <Grid container spacing={2} >
        <Grid item md={12} >
          <Item sx={{backgroundColor:'yellow', height:180,}}><img width="180" height="180"  src="https://cdn.shop-list.com/res/up/shoplist/shp/__thum370__/ueno-shokai/435121302-60/1.jpg" alt="海の写真" title="空と海"/></Item>
        </Grid>
        <Grid item md={12} >
          <Item sx={{backgroundColor:'green', height:30,}}>NEW
【即納】シンプルビッグダウンOt1152</Item>
        </Grid>
        <Grid item xs={6} md={4}>
          <Item sx={{backgroundColor:'blue', height:30,}}>xs=6 md=4</Item>
        </Grid>
        <Grid item xs={6} md={8}>
          <Item sx={{backgroundColor:'green', height:30,}}>xs=6 md=4</Item>
        </Grid>
        <Grid item md={6}>
          <Item sx={{ height:30,}}><Button variant="outlined">Outlined</Button></Item>
        </Grid>
        <Grid item md={6}>
          <Item sx={{ height:30,}}><Button variant="contained">Contained</Button></Item>
        </Grid>
      </Grid>
    </Paper>
  );
}
