import React, { useState, useEffect } from 'react'
import {
  BrowserRouter,
  Routes,
  Route,
  Link,
  useNavigate,
  Navigate
} from "react-router-dom"

import axios from 'axios'
import { styled } from '@mui/material/styles';
import { useLocation, NavLink } from "react-router-dom";

import Navbar from './components/Navbar/Navbar'
import Expenses from "./Expenses"
import Invoices from "./Invoices"
import Products from "./components/Products/Products"
import ProductView from "./components/ProductView/productView"
import Cart from './components/Cart/Cart'
import Checkout from './components/CheckoutForm/Checkout/Checkout'
import Footer from './components/Footer/Footer'
import MySnackbar from './MySnackbar'
import OrderTable from './OrderTable'

import Pro from './Product'
import ProDet from './ProdcutDetail'
import Home from './Home'
import CartItem from './CartItem'
import SignUpa from './SignUp'
import SignIna from './SignIn'
import SearchBar from './SearchBar'
import SearchedItems from './SearchedItems'
import Checkouta from './Checkout'

import { ThemeProvider, createTheme } from '@mui/material/styles'
import { green } from '@mui/material/colors'
import Grid from '@mui/material/Grid'

import MyInfo from './MyInfo'
import SignIn from './components/SignInUp/SignIn'
import SignUp from './components/SignInUp/SignUp'
import useToken from './useToken'


import { Divider, Snackbar, Paper } from '@mui/material'

import {commerce} from './lib/commerce'

const theme = createTheme({
  palette: {
    primary: {
      main: '#330e62',
    },
    secondary: {
      main: green[500],
    },
  },
  typography: {
    fontFamily: [
      '-apple-system',
      'BlinkMacSystemFont',
      '"Segoe UI"',
      'Roboto',
      '"Helvetica Neue"',
      'Arial',
      'sans-serif',
      '"Apple Color Emoji"',
      '"Segoe UI Emoji"',
      '"Segoe UI Symbol"',
    ].join(','),
  },

})

const Item = styled(Paper)(({ theme }) => ({
  backgroundColor: theme.palette.mode === 'dark' ? '#1A2027' : '#fff',
  ...theme.typography.body2,
  padding: theme.spacing(1),
  textAlign: 'center',
  color: theme.palette.text.secondary,
}));

const API_URL='http://localhost:3001'


export default function App() {

  const [products, setProducts] = useState([])
  const [cart, setCart] = useState({})
  const [order, setOrder] = useState({})
  const [errorMessage, setErrorMessage] = useState('')

  const [open, setOpen] = React.useState(false)
  const [snackbarText, setSnackbarText] = React.useState('')
  const [searched, setSearched] = React.useState(true)
  const [searchTerm, setSearchTerm] = React.useState('')



  // myinfo 
  const [ loading, setLoading ] = useState(false)
  const [ customer, setCustomer ] = useState({})
  const { setToken } = useToken() // save in localStorage

  // orders
  const [rows, setRows] = useState({})

  useEffect(() => {
    fetchProductsWithParams()
    fetchCart()
    fetchCustomerInfo()
    fetchOrderHistories()
  }, []) // [] is for useEffectをマウント時に1回だけ実行する方法


  const fetchCustomerInfo = async () => {
    /*
    const ok = await commerce.customer.isLoggedIn()
    console.log(ok)
    const  data  = await commerce.customer.about()
    console.log(data)
    setLoading(ok)
    setCustomer(data)*/
    let customerId=localStorage.getItem('token')
    console.log(customerId)
    customerId=customerId.substring(1,customerId.length-1)
    console.log(customerId)
    const url = new URL(
      "https://api.chec.io/v1/customers/"+customerId
  );

  let headers = {
      "X-Authorization": "sk_test_39980259115a0d9753812433d6740aa60b83dc9a64fba",
      "Accept": "application/json",
      "Content-Type": "application/json",
  };

  fetch(url, {
      method: "GET",
      headers: headers,
  })
      .then(response => response.json())
      .then(json => {console.log(json);setCustomer(json);});
  }


  const createCustomer =  async (event) => {
    event.preventDefault();
    const data = new FormData(event.currentTarget);
    for (var value of data.values()) {
        console.log(value);
    }
    let firstname=data.get('firstname')
    let lastname=data.get('lastname')
    let email=data.get('email')
    let password=data.get('password')

    const url = new URL(
      "https://api.chec.io/v1/customers"
  );

  let headers = {
      "X-Authorization": "sk_test_39980259115a0d9753812433d6740aa60b83dc9a64fba",
      "Content-Type": "application/json",
      "Accept": "application/json",
  };

  let body = {
      "email": email,
      "firstname": firstname,
      "lastname": lastname,
      "external_id": "MY_CRM_USER_123"
  }

  let customerId=undefined
  fetch(url, {
      method: "POST",
      headers: headers,
      body: JSON.stringify(body)
  })
      .then(response => response.json())
      .then(json => {
        console.log(json);
        customerId=json.id
        if(customerId!==undefined){
          //console.log("signup success")
          //Get customer
          const url = new URL(
            "https://api.chec.io/v1/customers/"+customerId
        );

        let headers = {
            "X-Authorization": "sk_test_39980259115a0d9753812433d6740aa60b83dc9a64fba",
            "Accept": "application/json",
            "Content-Type": "application/json",
        };

        fetch(url, {
            method: "GET",
            headers: headers,
        })
            .then(response => response.json())
            .then(json => {console.log(json);setToken(json.id);window.location.href="/";});
              //window.location.href="/"

        }else{
          console.log("signup failed")
        }
      });


  }


  const fetchOrderHistories = async () => {
    const data = await commerce.customer.getOrders();
    console.log(data)
    setRows(data.data)

  };

  const fetchProducts = async () => {
    const { data } = await commerce.products.list()
    console.log(data)
