import * as React from 'react';
import { useNavigate } from 'react-router-dom';
import { Box, Button, Checkbox, Link, Grid, Typography, Container, FormControlLabel, CssBaseline, ThemeProvider, TextField,  } from '@mui/material';

import useToken from './useToken'
import {commerce} from '../../lib/commerce'

let API_URL=process.env.REACT_APP_DEV_TOKEN_API_URL

async function loginUser(credentials) {
    //return fetch(API_URL+'/login', {
    return fetch('http://localhost:3002/login', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(credentials)
    })
      .then(data => data.json())
}


async function loginChec() {
  await commerce.customer.login('satoaikawa@qq.com', 'http://localhost:3000/registration-success').then((loginToken) => console.log(loginToken));
}

export default function SignIn() {

  const navigate = useNavigate();
  const { token, setToken } = useToken(); // save in localStorage

  const handleSubmit = async (event) => {
    event.preventDefault();
    const data = new FormData(event.currentTarget);
    for (var value of data.values()) {
        console.log(value);
      }
    // eslint-disable-next-line no-console
    console.log({
      email: data.get('email'),
      password: data.get('password'),
    });
    const token = await loginUser({
        email: data.get('email'),
        password: data.get('password'),
      });
    setToken(token);
    console.log(token)
    //navigate(-1)

  };

  return (

    <ThemeProvider >
      {/*<ThemeProvider theme={theme}>*/}
      <Container component="main" maxWidth="xs">
        <CssBaseline />
        <Box
          sx={{
            marginTop: 8,
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
          }}
        >
          {/*<Box component="form" onSubmit={handleSubmit} noValidate sx={{ mt: 1 }}>*/}
          <Box component="form" onSubmit={loginChec} noValidate sx={{ mt: 1 }}>
          <Typography component="h1" variant="h5">
          サインインする
          </Typography>
            <TextField
              margin="normal"
              required
              fullWidth
              id="email"
              label="Email Address"
              name="email"
              autoComplete="email"
              autoFocus
            />
            <TextField
              margin="normal"
              required
              fullWidth
              name="password"
              label="Password"
              type="password"
              id="password"
              autoComplete="current-password"
            />
            <FormControlLabel
              control={<Checkbox value="remember" color="primary" />}
              label="Remember me"
            />
            <Button
              type="submit"
              fullWidth
              variant="contained"
              sx={{ mt: 3, mb: 2 }}
            >
              サインイン
            </Button>
            <Grid container>
              <Grid item xs>
                <Link href="#" variant="body2">
                  Forgot password?
                </Link>
              </Grid>
              <Grid item>
                <Link href="#" variant="body2">
                  {"Don't have an account? Sign Up"}
                </Link>
              </Grid>
            </Grid>
          </Box>
        </Box>
      </Container>
    </ThemeProvider>
  );
}
