import * as React from 'react';
import { styled } from '@mui/material/styles';
import { useNavigate } from 'react-router-dom';
import { Box, Button, Checkbox, Link, Typography, Container, FormControlLabel, CssBaseline, ThemeProvider, TextField, Paper, Grid, Divider } from '@mui/material';

import ModalUnstyled from '@mui/base/ModalUnstyled';

const Item = styled(Paper)(({ theme }) => ({
  backgroundColor: theme.palette.mode === 'dark' ? '#1A2027' : '#fff',
  ...theme.typography.body2,
  padding: theme.spacing(1),
  textAlign: 'center',
  color: theme.palette.text.secondary,
}));

export default function Checkout() {
  return (
    <Paper elevation={0} sx={{
      flexGrow: 1,
      backgroundColor: (theme) => theme.palette.mode === 'dark' ? '#1A2027' : '#fff',
    }} >
      <Grid container spacing={0} justifyContent="center" alignItems="center">


        <Grid item md={8} >
        <Item elevation={0} >
        <Grid component="form" noValidate onSubmit={handleSubmit} container spacing={2} justifyContent='center' alignItems='center' >

        <Grid item xs={6} md={12}>
        <Item elevation={0} ><Typography variant="h5">お支払い方法</Typography></Item>
        </Grid>

        <Grid item xs={6} md={12}>
        <Item elevation={0} ><Typography variant="h5">ご連絡先</Typography></Item>
        </Grid>

        <Grid item xs={6} md={12}>
        <Item elevation={0} ><Typography variant="h5">お届け先</Typography></Item>
        </Grid>

        <Grid item xs={12} sm={12}>
        <TextField
        required
        fullWidth
        id="lastname"
        label="姓"
        name="lastname"
        autoComplete="family-name"
        />
        </Grid>

        <Grid item xs={12} sm={12}>
        <TextField
        autoComplete="given-name"
        name="firstname"
        required
        fullWidth
        id="firstname"
        label="名"
        autoFocus
        />
        </Grid>

        <Grid item xs={12}>
        <TextField
        required
        fullWidth
        id="email"
        label="メールアドレス（Gmail等のフリーメール推奨）"
        name="email"
        autoComplete="my-email"
        />
        </Grid>

      <Grid item xs={12}>
      <Button
      type="submit"
      fullWidth
      variant="contained"
      sx={{ margin:'auto', height:70 }}
      >
      支払う
      </Button>
      </Grid>

      </Grid>
      </Item>
      </Grid>


      <Grid item md={4} >
      <Item elevation={0} >
      <Grid item xs={6} md={12}>
        <Item elevation={0} ><Typography variant="h5">購入するアイテム</Typography></Item>
        </Grid>
      <Divider sx={{margin:'10px'}}/>
      <Grid item xs={6} md={12}>
        <Item elevation={0} ><ModalUnstyledDemo /></Item>
        </Grid>
      <Grid item xs={6} md={12}>
        <Item elevation={0} ><Typography variant="h5">合計:      JPY ￥6,100</Typography></Item>
        </Grid>
      <Divider sx={{margin:'10px'}}/>

      </Item>
      </Grid>


      </Grid>
    </Paper>
  );
}

// for testing form fubmission
const handleSubmit = (event) => {
  event.preventDefault();
  const data = new FormData(event.currentTarget);
  // eslint-disable-next-line no-console
  console.log({
    email: data.get('email'),
    password: data.get('password'),
  });
};






const StyledModal = styled(ModalUnstyled)`
  position: fixed;
  z-index: 1300;
  right: 0;
  bottom: 0;
  top: 0;
  left: 0;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const Backdrop = styled('div')`
  z-index: -1;
  position: fixed;
  right: 0;
  bottom: 0;
  top: 0;
  left: 0;
  background-color: rgba(0, 0, 0, 0.5);
  -webkit-tap-highlight-color: transparent;
`;

const style = {
  width: 400,
  bgcolor: 'background.paper',
  border: '2px solid #000',
  p: 2,
  px: 4,
  pb: 3,
};

function ModalUnstyledDemo() {
  const [open, setOpen] = React.useState(false);
  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);

  return (
    <div>
      <button type="button" onClick={handleOpen}>
        Open modal
      </button>
      <StyledModal
        aria-labelledby="unstyled-modal-title"
        aria-describedby="unstyled-modal-description"
        open={open}
        onClose={handleClose}
        BackdropComponent={Backdrop}
      >
        <Box sx={style}>
          <h2 id="unstyled-modal-title">配送ポリシー</h2>
          <p id="unstyled-modal-description">Aliquid amet deserunt earum!</p>
        </Box>
      </StyledModal>
    </div>
  );
}
