import React, { useState, useEffect } from 'react'
import {
  BrowserRouter,
  Routes,
  Route,
  Link,
  useNavigate,
  Navigate
} from "react-router-dom"

import axios from 'axios'
import { styled } from '@mui/material/styles';
import { useLocation, NavLink } from "react-router-dom";

import Navbar from './components/Navbar/Navbar'
import Expenses from "./Expenses"
import Invoices from "./Invoices"
import Products from "./components/Products/Products"
import ProductView from "./components/ProductView/productView"
import Cart from './components/Cart/Cart'
import Checkout from './components/CheckoutForm/Checkout/Checkout'
import Footer from './components/Footer/Footer'
import MySnackbar from './MySnackbar'
import OrderTable from './OrderTable'

import Pro from './Product'
import ProDet from './ProdcutDetail'
import Home from './Home'
import CartItem from './CartItem'
import SignUpa from './SignUp'
import SignIna from './SignIn'
import SearchBar from './SearchBar'
import SearchedItems from './SearchedItems'
import Checkouta from './Checkout'
import MyAccount from './MyAccount'

import { ThemeProvider, createTheme } from '@mui/material/styles'
import { green } from '@mui/material/colors'
import Grid from '@mui/material/Grid'

import MyInfo from './MyInfo'
import SignIn from './components/SignInUp/SignIn'
import SignUp from './components/SignInUp/SignUp'


import { Divider, Snackbar, Paper } from '@mui/material'

import {commerce} from './lib/commerce'

const theme = createTheme({
  palette: {
    primary: {
      main: '#330e62',
    },
    secondary: {
      main: green[500],
    },
  },
  typography: {
    fontFamily: [
      '-apple-system',
      'BlinkMacSystemFont',
      '"Segoe UI"',
      'Roboto',
      '"Helvetica Neue"',
      'Arial',
      'sans-serif',
      '"Apple Color Emoji"',
      '"Segoe UI Emoji"',
      '"Segoe UI Symbol"',
    ].join(','),
  },

})

const Item = styled(Paper)(({ theme }) => ({
  backgroundColor: theme.palette.mode === 'dark' ? '#1A2027' : '#fff',
  ...theme.typography.body2,
  padding: theme.spacing(1),
  textAlign: 'center',
  color: theme.palette.text.secondary,
}));

const API_URL='http://localhost:3001'


export default function App() {

  const [products, setProducts] = useState([])
  const [cart, setCart] = useState({})
  const [order, setOrder] = useState({})
  const [errorMessage, setErrorMessage] = useState('')

  const [open, setOpen] = React.useState(false)
  const [snackbarText, setSnackbarText] = React.useState('')
  const [searched, setSearched] = React.useState(true)
  const [searchTerm, setSearchTerm] = React.useState('')
  const [categories, setCategories] = React.useState([])



