import * as React from 'react';
import { styled } from '@mui/material/styles';
import {Box,Button, Paper, Grid, Rating, Divider,  ButtonGroup } from '@mui/material';
import { Input, InputAdornment } from '@mui/material';

import Accordion from '@mui/material/Accordion';
import AccordionSummary from '@mui/material/AccordionSummary';
import AccordionDetails from '@mui/material/AccordionDetails';
import Typography from '@mui/material/Typography';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import SearchIcon from '@mui/icons-material/Search';

import Product from './Product';

const Item = styled(Paper)(({ theme }) => ({
  backgroundColor: theme.palette.mode === 'dark' ? '#1A2027' : '#fff',
  ...theme.typography.body2,
  padding: theme.spacing(1),
  textAlign: 'center',
  color: theme.palette.text.secondary,
}));

export default function SimpleAccordion() {
  return (
    <div>
      <AppBar position="fixed">
      <Accordion elevation={0} expanded={true}>

        <AccordionSummary
          aria-controls="panel1a-content"
          id="panel1a-header"
        >
          <Typography>色々な条件で探してみる</Typography>
          <SearchIcon />
        </AccordionSummary>
        <AccordionDetails>
        <Grid container justifyContent="center" alignItems="center" spacing={2}>
        <Grid item md={12}>
        <Input
          type="text"
          placeholder="キーワードで探す"
          onChange={(event) => {
            //setSearchTerm(event.target.value);
          }}
          startAdornment={
            <InputAdornment position="start">
              <SearchIcon />
            </InputAdornment>
          }
        />
        </Grid>
        <Grid item md={12}>
        <Typography>
            おすすめキーワードはこちら
        </Typography>
        </Grid>
        <Grid item md={12}>
        <VariantButtonGroup elems={['ジャケット','ボア','レザー','ブルゾン','コート']} />
        </Grid>
      </Grid>
      <br/>
        </AccordionDetails>
      </Accordion>
      </AppBar>
    </div>
  );
}





function VariantButtonGroup({elems}) {
  const [selected, setSelected] = React.useState('')

  const handleChange = (event) => {
    setSelected(event.target.value)
  };

  return (
    <Box
      sx={{
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        '& > *': {
          m: 1,
        },
      }}
    >
      <ButtonGroup variant="text" aria-label="text button group">
                {elems.map((elem, idx) => (
                  <Button value={elem} sx={{backgroundColor:selected===elem?'black':'white', color:selected===elem?'white':'black'}} onClick={handleChange} >{elem}</Button>
                ))}
      </ButtonGroup>
    </Box>
  );
}
