import React, { useState, useEffect } from 'react';

import Grid from '@mui/material/Grid';

import Product from "./Product/Product.js";

import { Input, InputAdornment } from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';
import CategoryChips from '../../CategoryChips';


export default function Products({ products, onAddToCart }) {

  const [searchTerm, setSearchTerm] = useState("");

  const [chipData, setChipData] = React.useState([
    { key: 0, label: 'Angular' },
    { key: 1, label: 'jQuery' },
    { key: 2, label: 'Polymer' },
    { key: 3, label: 'React' },
    { key: 4, label: 'Vue.js' },
  ]);

    useEffect(() => {
        console.log(products)
      }, []);

    return (
      <div>
      <Grid container>
      <Input
        type="text"
        placeholder="本をキーワードで探す..."
        onChange={(event) => {
          setSearchTerm(event.target.value);
        }}
        startAdornment={
          <InputAdornment position="start">
            <SearchIcon />
          </InputAdornment>
        }
      />

    </Grid>
    <CategoryChips  chipData={chipData} setChipData={setChipData}  />
    <br/>
      <Grid container justify="center" spacing={5} >
        {products
          .filter((product) => {
            if (searchTerm === "") {
              return product;
            } else if (
              product.name
                .toLowerCase()
                .includes(searchTerm.toLocaleLowerCase())
            ) {
              return product;
            }
          })
          .filter((product) => {
            if (chipData.length === 0) {
              return product;
            } else if (
              chipData.forEach((chipVal, idx, array) => {
                if(product.categories.length===0)return false;
                if(product.categories.includes(chipVal)){
                  return true
                }
              })
            ) {
              return product;
            }
          })
          .map((product) => (
            <Grid item key={product.id} xs={12} sm={6} md={4} lg={3} id="pro">
              <Product product={product} onAddToCart={onAddToCart} />
            </Grid>
          ))}
      </Grid>
      </div>
    );
  }
