import * as React from 'react';
import { styled } from '@mui/material/styles';
import {Box,Button, Paper, Grid, Rating} from '@mui/material';
import Products from './components/Products/Products';
import Product from './Product';

const Item = styled(Paper)(({ theme }) => ({
  backgroundColor: theme.palette.mode === 'dark' ? '#1A2027' : '#fff',
  ...theme.typography.body2,
  padding: theme.spacing(1),
  textAlign: 'center',
  color: theme.palette.text.secondary,
}));
///Users/satoshiaikawa/react-mui-bookstore-k8s/src/assets/jacket.gif
export default function FullWidthGrid() {
  return (
    <Paper elevation={1} sx={{
      flexGrow: 1,
      backgroundColor: (theme) => theme.palette.mode === 'dark' ? '#1A2027' : '#fff',
    }} >
      <Grid container spacing={2} >
        <Grid item md={12} >
          <Item elevation={0} ><img width="360" height="360"  src="https://cdn.shop-list.com/res/up/shoplist/shp/__thum370__/ueno-shokai/435121302-60/1.jpg" alt="海の写真" title="空と海"/></Item>
        </Grid>
        <Grid container justify="center" spacing={5} >
        {[1,2,3,4,5,6,7,8].map((product,idx) => (
            <Grid item key={idx} lg={1} >
             <img width="60" height="60"  src="https://cdn.shop-list.com/res/up/shoplist/shp/__thum370__/ueno-shokai/435121302-60/1.jpg" alt="海の写真" title="空と海"/>
            </Grid>
          ))}
        </Grid>
        <Grid item md={12} >
          <Item elevation={0} sx={{height:30,}}>NEW【即納】シンプルビッグダウンOt1152</Item>
        </Grid>
        <Grid item xs={6} md={4}>
          <Item elevation={0} sx={{backgroundColor:'white', height:30,}}><Rating name="size-small" defaultValue={Math.floor(Math.random() * (5 - 1 + 1) + 1)} size="small" readOnly />
        ({Math.floor(Math.random() * (200 - 10 + 10) + 10)})</Item>
        </Grid>
        <Grid item xs={6} md={8}>
          <Item elevation={0} sx={{ height:30,}}>¥9,660</Item>
        </Grid>
        <Grid item md={6}>
          <Item elevation={0} sx={{ height:30,}}><Button variant="outlined">商品詳細</Button></Item>
        </Grid>
        <Grid item md={6}>
          <Item elevation={0} sx={{ height:30,}}><Button variant="contained">お取り扱い</Button></Item>
        </Grid>
        <Grid item md={12}>
          <Item elevation={0} sx={{ height:30,}}><Button variant="contained">カートに入れる</Button></Item>
        </Grid>
        <Grid item md={12}>
          <Item elevation={0} sx={{ height:30,}}>おすすめ関連アイテム</Item>
        </Grid>
        <Grid container justify="center" spacing={5} >
        {[1,2,3,4,5,6,7,8].map((product,idx) => (
            <Grid item key={idx} lg={4} >
            <Product/>
            </Grid>
          ))}
        </Grid>
        <Grid item md={12}>
          <Item elevation={0} sx={{ height:30,}}>この商品のレビュー</Item>
        </Grid>
      </Grid>
    </Paper>
  );
}
