          ops.push({vid:vidarr[idx], size:sizeDict[option.vgrp_7ZAMo1YrEoNJ4x], color:colorDict[option.vgrp_r2LM5Qq0R5ZV1g]})

        })
        console.log(ops)
        setVariants(ops)
      });
  }
*/

  const handleClick = (event, newValue) => {
    console.log(event.target.src)
    setSelectedImage(event.target.src)
    //const newImages=images.filter(image=>image!==event.target.src)
    //setImages(newImages)
  };  

  if(product.variant_groups===undefined)return "loading"

  return (
    <Paper elevation={1} sx={{
      flexGrow: 1,
      backgroundColor: (theme) => theme.palette.mode === 'dark' ? '#1A2027' : '#fff',
    }} >
      <Grid container spacing={2} >
        <Grid item md={12} >
          <Item elevation={0} ><img width="600" height="600"  src={selectedImage} alt="海の写真" title="空と海"/></Item>
        </Grid>
        <Grid container justify="center" spacing={5} >
        {images.map((elem, idx) => (
            <Grid item key={idx} lg={1} >
             <img  onClick={handleClick} width="60" height="60"  src={elem} alt="海の写真" title="空と海"/>
            </Grid>
          ))}
        </Grid>
        </Grid>

        <Grid container spacing={2} direction="column" alignItems="center" >
        <Grid item md={12} >
