import React, { useState, useEffect } from 'react'
import {
  BrowserRouter,
  Routes,
  Route,
  Link,
  useNavigate,
  Navigate
} from "react-router-dom"

import { styled } from '@mui/material/styles';

import Navbar from './components/Navbar/Navbar'
import Footer from './components/Footer/Footer'
import MySnackbar from './MySnackbar'

import ProductDetail from './ProdcutDetail'
import Home from './Home'
import CartItem from './CartItem'
import SignUpa from './SignUp'
import SignIna from './SignIn'
import SearchedItems from './SearchedItems'
import Checkout from './Checkout'
import MyAccount from './MyAccount'

import { ThemeProvider, createTheme } from '@mui/material/styles'
import { green } from '@mui/material/colors'

import { Divider, Snackbar, Paper } from '@mui/material'

import {commerce} from './lib/commerce'

const theme = createTheme({
  palette: {
    primary: {
      main: '#330e62',
    },
    secondary: {
      main: green[500],
    },
  },
  typography: {
    fontFamily: [
      '-apple-system',
      'BlinkMacSystemFont',
      '"Segoe UI"',
      'Roboto',
      '"Helvetica Neue"',
      'Arial',
      'sans-serif',
      '"Apple Color Emoji"',
      '"Segoe UI Emoji"',
      '"Segoe UI Symbol"',
    ].join(','),
  },

})

const Item = styled(Paper)(({ theme }) => ({
  backgroundColor: theme.palette.mode === 'dark' ? '#1A2027' : '#fff',
  ...theme.typography.body2,
  padding: theme.spacing(1),
  textAlign: 'center',
  color: theme.palette.text.secondary,
}));

const API_URL='http://localhost:3001'


export default function App() {


  const [cart, setCart] = useState({})
  const [order, setOrder] = useState({})
  const [errorMessage, setErrorMessage] = useState('')

  // snackbar
  const [open, setOpen] = React.useState(false)
  const [snackbarText, setSnackbarText] = React.useState('')





  // myinfo 
  const [ loading, setLoading ] = useState(false)
  const [ customer, setCustomer ] = useState({})

  // orders
  const [orderHistories, setOrderHistories] = useState({})

  useEffect(() => {
    //if(localStorage.getItem('cart_id')!==null)fetchCart()
    //fetchOrderHistories()
  }, []) // [] is for useEffectをマウント時に1回だけ実行する方法




  const createCustomer =  async (event) => {
    event.preventDefault();
    const data = new FormData(event.currentTarget);
    for (var value of data.values()) {
        console.log(value);
    }
    let firstname=data.get('firstname')
    let lastname=data.get('lastname')
    let email=data.get('email')
    let password=data.get('password')

    const url = new URL(
      "https://api.chec.io/v1/customers"
  );

  let headers = {
      "X-Authorization": "sk_test_39980259115a0d9753812433d6740aa60b83dc9a64fba",
      "Content-Type": "application/json",
      "Accept": "application/json",
  };

  let body = {
      "email": email,
      "firstname": firstname,
      "lastname": lastname,
      "external_id": "MY_CRM_USER_123"
  }

  let customerId=undefined
  fetch(url, {
      method: "POST",
      headers: headers,
      body: JSON.stringify(body)
  })
      .then(response => response.json())
      .then(json => {
        console.log(json);
        customerId=json.id
        if(customerId!==undefined){
          //console.log("signup success")
          //Get customer
          const url = new URL(
            "https://api.chec.io/v1/customers/"+customerId
        );

        let headers = {
            "X-Authorization": "sk_test_39980259115a0d9753812433d6740aa60b83dc9a64fba",
            "Accept": "application/json",
            "Content-Type": "application/json",
        };

        fetch(url, {
            method: "GET",
            headers: headers,
        })
            .then(response => response.json())
            .then(json => {
              //console.log(json);
              localStorage.setItem('token',json.id)
              setCustomer(json)

              // TODO add password, email, customerId pair to auth API here

              // add chec customer to self-proviced auth API to allow authentication with email and password
              //addCustomerToAuthApi({customer_id:resultChec.id, email:resultChec.email, firstname, lastname, password})
              const customerId=json.id
              const url2 = new URL(
                "http://localhost:3002/api/register/"+customerId+"/"+email+"/"+firstname+"/"+lastname+"/"+password
              );

              let headers2 = {
                "Content-Type": "application/json  charset=UTF-8",
                "Accept": "application/json",
              };

              fetch(url2, {
                method: "GET",
                headers: headers2
              })
              .then(response => response.json())
              .then(json => {
                  console.log(json);
                  const createCart = async () => {
                    // Retrieve the customers current cart (tracked by their browser)
                    await commerce.cart.refresh()
                    .then(cart => {
                        console.log(cart)
                        setCart(cart)
                        localStorage.setItem('cart_id', cart.id)
                        const url3 = new URL(
                          "http://localhost:3002/api/cart/"+customerId+"/"+cart.id
                        );

                        let headers3 = {
                          "Content-Type": "application/json  charset=UTF-8",
                          "Accept": "application/json",
                        };

                        fetch(url3, {
                          method: "GET",
                          headers: headers3
                        })
                        .then(response => response.json())
                        .then(json => {
                            console.log(json);
                            window.location.href="/"
                        });

                    });
                  }
                  createCart()

              });

              });

        }else{
          console.log("signup failed")
        }
      });


  }


  const fetchOrderHistories = async () => {
      let customerId=null
      customerId=localStorage.getItem('token')
      //customerId=customerId.substring(1,customerId.length-1)
      const url = new URL(
        "https://api.chec.io/v1/customers/"+customerId
      );

      let headers = {
        "X-Authorization": "sk_test_39980259115a0d9753812433d6740aa60b83dc9a64fba",
        "Accept": "application/json",
        "Content-Type": "application/json",
      };

      fetch(url, {
        method: "GET",
        headers: headers,
      })
      .then(response => response.json())
      .then(json => {
          console.log(json)
          setOrderHistories(json)
      });

  };

  const handleCaptureCheckout = async (checkoutTokenId, newOrder) => {
    try {
      // Captures an order and payment by converting a checkout token and necessary data into an order object, and charging all related transactions.
      const incomingOrder = await commerce.checkout.capture(checkoutTokenId, newOrder)
      console.log(incomingOrder)

      setOrder(incomingOrder)

      //refreshCart()
      fetchOrderHistories()

    } catch (error) {
      setErrorMessage(error.data.error.message)
    }
  }


  const handleSignIn =  async (event) => {
    event.preventDefault();
    const data = new FormData(event.currentTarget);
    for (var value of data.values()) {
        console.log(value);
    }
    let email=data.get('email')
    let password=data.get('password')

    // login with self-provided auth API
    //let result=fetchCustomerFromAuthApi({email, password})
    const url1 = new URL(
      "http://localhost:3002/api/login/"+email+"/"+password
    );
    let headers1 = {
      "Content-Type": "application/json  charset=UTF-8",
      "Accept": "application/json",
    };

    fetch(url1, {
      method: "GET",
      headers: headers1
    })
    .then(response => response.json())
    .then(json => {
      console.log(json);
      if(json.customer_id!==undefined){
        localStorage.setItem('token', json.customer_id)
        const fetchCartByCartId = async (cartId) => {
          await commerce.cart.retrieve(cartId)
          .then(cart => {
            console.log(cart)
            setCart(cart)
          });
        }
        fetchCartByCartId(json.cart_id)
        localStorage.setItem('cart_id', cart.id)
        window.location.href="/"

      }else{
        console.log("signin failed")
      }
    });

}


  return (
    <div >
  <ThemeProvider theme={theme}>
  <BrowserRouter>
      <Navbar totalItems={cart.total_items} cusotmer={customer} />
      <br/>
      <br/>
      <br/>
      <MySnackbar open={open} setOpen={setOpen} snackbarText={snackbarText} />
      <Divider sx={{margin:'10px'}}/>
      <Routes>
      <Route exact path="/" element={<Home />} />
      <Route exact path="/collections/:slug" element={<SearchedItems />} />

      <Route exact path="/signin" element={<SignIna onSignIn={handleSignIn} />} />
      <Route exact path="/signup" element={<SignUpa createCustomer={createCustomer} />} />
      <Route exact path="/cart" element={<CartItem />} />
      <Route exact path="/products/:product" element={<ProductDetail />} />
      <Route exact path="/checkout" element={<Checkout customer={customer} cart={cart} onCaptureCheckout={handleCaptureCheckout} />} />
      <Route exact path="/account" element={<MyAccount orders={orderHistories} />} />

      </Routes>
      <Footer title={"Online Fashion Store with React + MUI + k8s"} description={"S.AIKAWA"} />
  </BrowserRouter>
  </ThemeProvider>
    </div>
  )
}
