import * as React from 'react';
import { styled } from '@mui/material/styles';
import { Paper, Grid, Button } from '@mui/material';
import { Link } from 'react-router-dom';

import MyInfo from './MyInfo'
import Products from './components/Products/Products';
import CategoryChips from './CategoryChips';

const Item = styled(Paper)(({ theme }) => ({
  backgroundColor: theme.palette.mode === 'dark' ? '#1A2027' : '#fff',
  ...theme.typography.body2,
  padding: theme.spacing(1),
  textAlign: 'center',
  color: theme.palette.text.secondary,
}));

export default function Invoices({ products, onAddToCart }) {


  const [chipData, setChipData] = React.useState([
    { key: 0, label: 'Angular' },
    { key: 1, label: 'jQuery' },
    { key: 2, label: 'Polymer' },
    { key: 3, label: 'React' },
    { key: 4, label: 'Vue.js' },
  ]);

    return (
  <Grid container spacing={2} >
    <Grid item xs={8}>
    <Item sx={{height:150}}><Button variant="outlined" component={Link} to="/cart" >買い物かごを見る</Button></Item>
  </Grid>
  <Grid item xs={4}>
    <Item sx={{height:150}}><MyInfo /></Item>
  </Grid>
  <Grid item xs={8}>
    <Item><CategoryChips chipData={chipData} setChipData={setChipData} /></Item>
  </Grid>
  <Grid item xs={4}>
    <Item>xs=4</Item>
  </Grid>
  <Grid item xs={12}>
    <Item><Products products={products}  onAddToCart={onAddToCart} handleUpdateCartQty /></Item>
  </Grid>
  </Grid>
    );
  }
