import * as React from 'react';
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Button from '@mui/material/Button';
import { Typography, Divider, Grid } from '@mui/material';

import { Link } from 'react-router-dom';

export default function ImgMediaCard({product, onAddToCart}) {
  return (
    <Card sx={{ maxWidth: 345 }}>
      <CardMedia
        component="img"
        alt="im image"
        height="300"
        image={product.media.source}
        //image="https://cdn.chec.io/merchants/28663/assets/jmHrbyaTb3c6aRym%7Cbook1.jpg"
      />
      <CardContent         
        sx={{
          display: 'inline',
        }}>
        <Typography gutterBottom variant="h5" component="div">
        {product.name}
        </Typography>
        <Typography variant="h6" color="text.secondary">
          JPY <b>{product.price.formatted}</b>
        </Typography>
      </CardContent>
      <CardActions        
       sx={{
          display: 'inline',
        }}>
        <Button sx={{
          margin: '5px',
        }} varient="outlined"  size="small" component={Link} to={"/product-view/"+product.id} aria-label="add to cart">Show Details</Button>
  <Divider orientation="vertical" flexItem/>
        <Button sx={{
          margin: '5px',
        }} variant="contained" size="small" onClick={() => onAddToCart(product.id, 1)}>Add Cart</Button>
  <Grid container justifyContent="space-evenly">
  <Grid item xs>
  <Button variant="outlined" size="small" component={Link} to='/' >戻る</Button>
  </Grid>
  <Divider orientation="vertical" flexItem/>
  <Grid item xs>
  <Button variant="contained" size="small" onClick={() => onAddToCart(product.id, 1)}>Add Cart</Button>
  </Grid>
</Grid>
      </CardActions>
    </Card>
  );
}
